import { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import toast from "react-hot-toast";

function AdminProfile() {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem("token");
        if (!token) {
          toast.error("Please login first");
          navigate("/login");
          return;
        }

        const res = await axios.get("http://localhost:4000/api/auth/profile", {
          headers: { Authorization: `Bearer ${token}` }
        });

        if (res.data.success) {
          setUser(res.data.user);
        } else {
          toast.error("Unauthorized");
          navigate("/login");
        }
      } catch (err) {
        console.error(err);
        toast.error("Error fetching profile");
        navigate("/login");
      }
    };

    fetchProfile();
  }, [navigate]);

  if (!user) {
    return <div className="text-center p-10 text-xl">Loading profile...</div>;
  }

  return (
    <div className="min-h-screen bg-blue-100 flex items-center justify-center">
      <div className="bg-white p-8 rounded-xl shadow-lg text-center">
        <h1 className="text-3xl font-bold mb-4">Welcome Admin ðŸ‘‹</h1>
        <h2 className="text-xl mb-4 text-blue-600">{user.username}</h2>
        <button
          onClick={() => navigate("/admin")}
          className="bg-blue-500 text-white px-6 py-2 rounded hover:bg-blue-600"
        >
          Go to Admin Panel
        </button>
      </div>
    </div>
  );
}

export default AdminProfile;
