import axios from "axios";
import { useState, useEffect } from "react";
import toast from "react-hot-toast";
import { Link } from "react-router-dom";

function AdminDashboard({ user }) {
  const [allData, setAllData] = useState({
    users: [],
    managers: [],
    restaurants: []
  });
  const [loading, setLoading] = useState(false);

  // Fetch all data concurrently
  const fetchAllData = async () => {
    setLoading(true);
    try {
      const [usersRes, managersRes, restaurantsRes] = await Promise.all([
        axios.get("http://localhost:4000/api/auth/getallusers"),
        axios.get("http://localhost:4000/api/auth/getmanagers"),
        axios.get("http://localhost:4000/api/auth/getrestaurants")
      ]);

      if (
        usersRes.data.success &&
        managersRes.data.success &&
        restaurantsRes.data.success
      ) {
        toast.success("All data fetched successfully!");
        setAllData({
          users: usersRes.data.users || [],
          managers: managersRes.data.managers || [],
          restaurants: restaurantsRes.data.restaurants || []
        });
      } else {
        toast.error("Some data fetch failed");
      }
    } catch (err) {
      console.error(err);
      toast.error("Error fetching data");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchAllData();
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 via-blue-50 to-blue-100 p-5">
      <div className="max-w-6xl mx-auto">
        {/* Welcome Section */}
        <div className="bg-white p-6 rounded-lg shadow mb-8 text-center">
          <h1 className="text-3xl font-bold text-gray-800">
            Welcome, <span className="text-blue-600">{user.username || user.email}</span>!
          </h1>
          <p className="text-gray-600">Role: <strong>{user.role}</strong></p>
        </div>

        {/* Back Button */}
        <div className="flex justify-center gap-4 mb-8">
          <Link to="/">
            <button className="bg-blue-500 px-6 py-2 rounded-full text-white font-medium shadow-md transition-transform hover:scale-105">
              Back to Home
            </button>
          </Link>
        </div>

        {/* Stats */}
        <div className="grid grid-cols-1 sm:grid-cols-3 gap-6 mb-10">
          {["Users", "Managers", "Restaurants"].map((label, idx) => {
            const count = allData[label.toLowerCase()].length;
            return (
              <div key={label} className="bg-white p-5 rounded-lg shadow-md border">
                <h2 className="text-xl font-semibold text-blue-700 mb-2">Total {label}</h2>
                <p className="text-3xl text-center text-green-600">{count}</p>
              </div>
            );
          })}
        </div>

        {/* Users Table */}
        <div className="bg-white/80 p-6 rounded-lg shadow-md mb-10">
          <h2 className="text-2xl font-semibold text-blue-700 mb-5">All Users</h2>
          {allData.users.length > 0 ? (
            <div className="overflow-x-auto">
              <table className="min-w-full text-left border-collapse">
                <thead>
                  <tr>
                    <th className="border p-2">Username</th>
                    <th className="border p-2">Email</th>
                    <th className="border p-2">Role</th>
                  </tr>
                </thead>
                <tbody>
                  {allData.users.map((u) => (
                    <tr key={u._id}>
                      <td className="border p-2">{u.username}</td>
                      <td className="border p-2">{u.email}</td>
                      <td className="border p-2">{u.role}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <p>No users found.</p>
          )}
        </div>

        {/* Other Sections */}
        <SectionList title="All Restaurants" data={allData.restaurants} />
        <SectionList title="All Managers" data={allData.managers} isManager />

      </div>
    </div>
  );
}

// Reusable section component
function SectionList({ title, data, isManager = false }) {
  return (
    <div className="bg-white/80 p-6 rounded-lg shadow-md mb-10">
      <h2 className="text-2xl font-semibold text-blue-700 mb-5">{title}</h2>
      {data.length > 0 ? (
        <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-5">
          {data.map((item) => (
            <div key={item._id} className="bg-white p-4 rounded-lg shadow border">
              {isManager ? (
                <>
                  <p><strong>Name:</strong> {item.Name}</p>
                  <p><strong>Aadhaar:</strong> {item.AdharNumber}</p>
                  <p><strong>Phone:</strong> {item.PhoneNumber}</p>
                  <p><strong>PAN:</strong> {item.PanNumber}</p>
                </>
              ) : (
                <>
                  <p><strong>Name:</strong> {item.name}</p>
                  <p><strong>Address:</strong> {item.address}</p>
                  <p><strong>Special For:</strong> {item.specialfor}</p>
                </>
              )}
            </div>
          ))}
        </div>
      ) : (
        <p>No {title.toLowerCase()} found.</p>
      )}
    </div>
  );
}

export default AdminDashboard;
